Favorite);?>																<li> 																	<a href="javascript:void(0);" class="deleteFav" id="deleteFav-<?php echo $favResult['user_id'];?>">																		<span class="dlt"></span>Delete																	</a>																</li>															</ul>														</li>														</ul>														</div>													</div>												</div>											</div>										</div>										<?php //echo $i;										}} ?>										<div class="rated_sec last pull-left visitor_profile">											<?php if($addedMeFavCount != 0){?>											<div class="rs edit" id="fav-edit">												<div id="edit">													<span><a href="javascript:void(0);"> <i class="fa fa-pencil"></i> Edit</a></span>												</div>												<div id="delet">													<span class="" id="cancle"><a href="javascript:void(0);">Cancel</a></span>													<span class="margin-r10"><a href="javascript:void(0);" id="deleteAllFav"><i class="fa fa-trash-o"></i> Delete</a></span>																										<div class="select_all" id="select_all">														<form>															<div>																<div class="radio my-radio">																	<div class="select"><b>Select</b></div>																	<div class="swith_gq">																		<input type="radio" name="option" data-rel="#general" id="selectAllFav" class="my-radio">																		<label for="selectAllFav"><span><span></span></span>All</label>																	</div>																	<div class="swith_gq">																		<input type="radio" name="option" data-rel="#general" id="unselectAllFav" class="my-radio">																		<label for="unselectAllFav"><span><span></span></span>None</label>																	</div>																</div>															</div>														</form>													</div>												</div>											</div>											<?php } ?>											<div class="align-c rs_bg_last"><?php													$diff=$addedMeFavCount-$i;