#DELECT ALL FAVORITES##########################		$("#selectAllFav").live('click' ,function () {			$(".favCheckbox").each(function(){				this.checked = true;			});		});		$("#unselectAllFav").live('click' ,function () {			$(".favCheckbox").each(function(){				this.checked = false;			});		});		$("#deleteAllFav").live('click' ,function () {			if($("input[name='favCheckbox']:checked").length==0){				$('#error_message_header').html('Alert');				$('#error_message').html('Please select atleast one user.');				$('#alert_modal').modal('show');				return false;			}			var userIds = [];			$("input[name='favCheckbox']:checked").each(function() {				userIds.push($(this).val());			});			alert(userIds);			$.ajax({				type:"POST",				url:"ajax/favorite.php",				data:{action:'deleteAllFav' , userIds:userIds},				success:function(result){					if(result==1){						window.location.href='added-as-favorite.php';					}else{						$('#error_message_header').html('Alert');						$('#error_message').html('Problem in network.please try again.');						$('#alert_modal').modal('show');					}				}			});			return false;		});		});</script></body></html>