ileObj->getTodayMessageCount($user_id);				if($stickerPerCount <= 6){					$perForSticker=round(($stickerPerCount/6)*100 ,1);					$remaingCount = 6 - $stickerPerCount;					if($remaingCount != 6 && $remaingCount != 0) $remaingText = ' (message '.$remaingCount.' more people)';				}else{					$perForSticker = 100;				}			}			else if($reward_id == 5){				$stickerPerCount = $userProfileObj->getTodayRewardCount($user_id);				if($stickerPerCount){					$perForSticker = 100;				}else{					$perForSticker = 0;				}							}			else{				$perForSticker=0;			}						$lastRewardQuery = mysql_query("SELECT * FROM sr_user_reward WHERE user_id = '".$user_id."' AND reward_id ='".$reward_id."'");			$lastRewardCount = mysql_num_rows($lastRewardQuery);			$lastRewardResult = mysql_fetch_assoc($lastRewardQuery);		?>			<li>				<?php if($allStickerResult['user_id']){?>					<div class="icon pull-left">						<img src="images/reward/<?php echo $allStickerResult['reward_icon'];?>" data-target="#go4sticker<?php echo $allStickerResult['reward_id'];?>" onclick="$('#getMoreSticker').modal('hide');" style="cursor:pointer;" data-toggle="modal"/>					</div>					<div class="intro pull-left">						<h5><b><?php echo $allStickerResult['reward_title'];?></b></h5>						<p>Awarded on <?php echo date('d F Y' , strtotime($allStickerResult['awarded_on']));?></p>						<h5><small><b><?php echo $allStickerResult['reward_text'];?></b></small></h5>					</div>					<div class="progress stickers">						<div class="progress-bar stickers" role="progressbar" aria-valuenow="70"							aria-valuemin="0" aria-valuemax="100" style="width:<?php echo $perForSticker;?>%">							<span class="sr-only stickers"><?php echo $perForSticker;?>% Complete</span>							<div class="clearfix"></div>						</div>						<div class="clearfix"></div>						<p>Progress today <b><?php echo $perForSticker;?>%</b> </p>					</div>				<?php } else { 					if($lastRewardCount){ ?>					<div class="icon pull-left">						<img src="images/reward/<?php echo $allStickerResult['reward_icon'];?>" data-target="#go4sticker<?php echo $allStickerResult['reward_id'];?>" onclick="$('#getMoreSticker').modal('hide');" style="cursor:pointer;" data-toggle="modal" />					</div>					<div class="intro pull-left">						<h5><b><?php echo $allStickerResult['reward_title'];?></b></h5>						<p>Last Awarded on <?php echo date('d F Y' , strtotime($lastRewardResult['awarded_on']));?></p>						<p><?php echo $allStickerResult['reward_text'].$remaingText;?></p>					</div>				<?php } else{?>					<div class="icon pull-left"><img src="images/q.png"/></div>					<div class="intro pull-left">						<h5><b><?php echo $allStickerResult['reward_title'];?></b></h5>						<p><?php echo $allStickerResult['reward_text'].$remaingText;?></p>					</div>				<?php } ?>					<div class="progress stickers">						<div class="progress-bar stickers" role="progressbar" aria-valuenow="70"							aria-valuemin="0" aria-valuemax="100" style="width:<?php echo $perForSticker;?>%">							<span class="sr-only stickers"><?php echo $perForSticker;?>% Complete</span>							<div class="clearfix"></div>						</div>						<div class="clearfix"></div>							<p>Progress today <b><?php echo $perForSticker;?>%</b> </p>							<p>Number of credits to win <b>20</b> </p>							<p>Time remaining 							<b> 							<?php 							$left = mktime(23,59,59) - time() +1;							if($left < 120) echo '1 minute';							if($left < 3600) echo floor($left / 60) . ' minutes';							if($left < 7200) echo '1 hour';							if($left < 86400) echo floor($left / 3600) . ' hours';							?>							</b> </p>					</div>				<?php }?>			</li>		<?php } ?>			</ul>	</div>				<?php 	exit;}?>