ers.location_lat=$lat AND sr_users.location_lon=$lon AND sr_users.is_account_freeze = 0	AND  sr_fame_reel.user_id=sr_user_photo.user_id AND sr_user_photo.is_profileImage=1 AND sr_user_photo.status=1 ");			}		}		if( $query )		{			while( $result = mysql_fetch_array($query) )			{				$array[] = $result;			}			}		return $array;	}	function time2str($ts)	{		//echo $ts;		if(!ctype_digit($ts))			$ts = strtotime($ts);		 $diff = time() - $ts;		if($diff == 0)			return 'just now';		elseif($diff > 0)		{			$day_diff = floor($diff / 86400);			if($day_diff == 0)			{				if($diff < 60) return 'just now';				if($diff < 120) return '1 minute ago';				if($diff < 3600) return floor($diff / 60) . ' minutes ago';				if($diff < 7200) return '1 hour ago';				if($diff < 86400) return floor($diff / 3600) . ' hours ago';			}			if($day_diff == 1) return 'Yesterday';			if($day_diff < 7) return $day_diff . ' days ago';			if($day_diff < 31)				if(ceil($day_diff / 7)==1)return ' 1 week ago';				else return ceil($day_diff / 7) . ' weeks ago';			if($day_diff < 60) return ' last month';			return date('F Y', $ts);		}		else		{			$diff = abs($diff);			$day_diff = floor($diff / 86400);			if($day_diff == 0)			{				if($diff < 120) return 'in a minute';				if($diff < 3600) return 'in ' . floor($diff / 60) . ' minutes';				if($diff < 7200) return 'in an hour';				if($diff < 86400) return 'in ' . floor($diff / 3600) . ' hours';			}			if($day_diff == 1) return 'Tomorrow';			if($day_diff < 4) return date('l', $ts);			if($day_diff < 7 + (7 - date('w'))) return 'next week';			if(ceil($day_diff / 7) < 4) return 'in ' . ceil($day_diff / 7) . ' weeks';			if(date('n', $ts) == date('n') + 1) return 'next month';			return date('F Y', $ts);		}	}		function isFameReel( $user_id )	{		$returnVal = 0;		$query = mysql_query("SELECT * FROM sr_fame_reel WHERE user_id='".$user_id."'");				if( $query )		{			$returnVal = mysql_num_rows($query);		}		return $returnVal;	}			//##################################BLOCK###########################################	function userBlockCount($user_id){		$block_count = 0;		$query=mysql_query("SELECT count(block_id) as block_count FROM `sr_user_block` WHERE block_by='".$user_id."'"); 		if($query){			$result=mysql_fetch_object($query);			$block_count=$result->block_count;			}		return $block_count;	}	function isBlockUser($user_id , $block_by){		$returnVal = 0;		$query=mysql_query("SELECT * FROM sr_user_block WHERE user_id='".$user_id."' AND block_by='".$block_by."'");		if($query){			$returnVal = mysql_num_rows($query);		}		return $returnVal;	}	function isMeBlocked($user_id, $block_by)		{			$returnVal = 0;			$query=mysql_query("SELECT * FROM sr_user_block WHERE user_id='".$block_by."' AND block_by='".$user_id."'");			if($query){				$returnVal = mysql_num_rows($query);			}			return $returnVal;		}	function getBlockMeDetail($user_id,$block_by)	{		$returnVal='';		$query=mysql_query("SELECT * FROM sr_user_block WHERE user_id='".$block_by."' AND block_by='".$user_id."'");		if($query){			$returnVal = mysql_fetch_array($query);		}		return $returnVal;	}	function getBlockUser($user_id){		$array = array();		$query=mysql_query("SELECT  u.user_id,u.user_name,u.profile_image,u.dob,u.gender , u.is_online,u.here_to,u.location ,b.block_on FROM `sr_user_block` b,sr_users u WHERE b.block_by='".$user_id."' AND b.user_id=u.user_id AND b.status=1 ORDER BY b.block_on DESC"); 		if($query){			while($result=mysql_fetch_assoc($query)){				$array[]=$result;			}			}		return $array;	}	function getOnlineUser($user_id){		$array = array();		$userinfo=$this->getUserInfo($user_id);		$userLocation_lat=$userinfo['location_lat'];		$userLocation_lon=$userinfo['location_lon'];		$query=mysql_query("SELECT user_id, user_name, profile_image, dob, gender,is_o