 == 1) return 'Tomorrow';			if($day_diff < 4) return date('l', $ts);			if($day_diff < 7 + (7 - date('w'))) return 'next week';			if(ceil($day_diff / 7) < 4) return 'in ' . ceil($day_diff / 7) . ' weeks';			if(date('n', $ts) == date('n') + 1) return 'next month';			return date('d F Y', $ts);		}	}		function getCurrency(){		$result=array();		$query = mysql_query("SELECT * FROM sr_currency WHERE status = 1");		if($query){			while($result=mysql_fetch_assoc($query)){				$array[]=$result;			}			}		return $array;		}		#Log Details	function logDetails($userID = '')	{		$array = array();		$query = mysql_query("Select * from sr_user_login where user_id = '".$userID."' ");		if( mysql_num_rows($query)>0 )		{			while( $queryFetch = mysql_fetch_assoc($query) ){				$array[] = $queryFetch;			}		}		return $array;	}			# SEND MAIL TO USERS TO JOIN START RISHTA	function sendAddFriendMail( $user_email , $user_id )	{		$senderDet = $this->getUserInfo($user_id);		$objEmail = new Email();		$objEmail->mailaccount='start_rishta';		$objEmail->to=$user_email;		$objEmail->from=ADMINMAIL2;		$objEmail->fromname=Email_FROMNAME;		$objEmail->bcc=Email_BCC;		$objEmail->subject = "Someone you know wants you to join Startrishta!";		$objEmail->body = '<html>		<head>			<title></title>			<style type="text/css">		    	body {margin: 0; padding: 0; min-width: 100%!important;}		    	.content {width: 100%; max-width: 600px;}		    </style>		</head>		<body><!-- font-family: Open Sans,sans-serif; -->		<table width="100%" style="max-width:600px; margin:auto; " bgcolor="#E36356" border="0" cellpadding="0" cellspacing="0">			<tr>				<td style="padding-top:30px;">					<table width="95%" style="max-width:600px; margin:auto;" bgcolor="#ffffff" border="0" cellpadding="0" cellspacing="0">						<tr>							<td style="max-width:100%; width:100%; padding-top:20px;"><img src='.SITEURL.'images/logo3.png style