ge(img[j], 0, 0, w, h);		ctx[j].drawImage($(""+canvasId+"")[0], 0, 0, w, h, 0, 0, $(""+canvasId+"")[0].width, $(""+canvasId+"")[0].height);	}}</script></body></html>