iv>							<div class="caps_tag"><img src="images/active.png" height="20px" width="20px"/></div>							<div class="caps_pics">								<a href="javascript:void(0);">									<i class="fa fa-camera"></i>									<span><?php echo $userProfileObj->getPublicPhotoCount($photoReel['user_id']);?></span>								</a>							</div>						</div>					<?php }					} ?>					</div>				</div>				</div>			</div>			<div class="get_me_here">				<i class="fa fa-stop"></i><i class="rotate fa fa-stop"></i>			<?php //if($isFameReel){ ?>				<div class="gmh_pic" data-toggle="tooltip" data-placement="bottom" title="Add your photo here to get seen by everyone!">					<img src="<?php echo $userProfileImage;?>">					<div class="caption">						<a href="javascript:void(0);" class="credit-popup go-credit-get-me-here">GET<br /> ME<br /> HERE</a>					</div>				</div>			</div>					</div>	</div></section>